        .loading-screen {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-color: #eee;
            background-color: rgba(238, 238, 238, 0.9);
            z-index: 100;
        }
        .loading-main {
            position: absolute;
            top: 50%;
            left: 50%;
            -webkit-transform: translate(-50%, -50%);
            -ms-transform: translate(-50%, -50%);
            transform: translate(-50%, -50%);
        }

        .loading-spinner {
            position: relative;
        }
        .loading-spinner:before, .loading-spinner:after {
            content: '';
            position: relative;
            display: block;
        }
        .loading-spinner:before {
            -webkit-animation: spinner 2.5s cubic-bezier(0.75, 0, 0.5, 1) infinite normal;
            animation: spinner 2.5s cubic-bezier(0.75, 0, 0.5, 1) infinite normal;
            width: 60px;
            height: 60px;
            background-color: #9FCA51;
            background-image: url(https://static.ticketbox.vn/site/global/content-v2/img/ticketbox-logo.png?v1);
            background-repeat: no-repeat;
            background-size: 100% 100%;
            border-radius: 8px;
        }
        .loading-spinner:after {
            -webkit-animation: shadow 2.5s cubic-bezier(0.75, 0, 0.5, 1) infinite normal;
            animation: shadow 2.5s cubic-bezier(0.75, 0, 0.5, 1) infinite normal;
            bottom: -.5em;
            height: 7px;
            border-radius: 50%;
            background-color: #bbb;
        }

        @keyframes spinner {
            50% {
                -webkit-transform: rotate(360deg);
                -ms-transform: rotate(360deg);
                transform: rotate(360deg);
            }
            100% {
                -webkit-transform: scale(1) rotate(720deg);
                -ms-transform: scale(1) rotate(720deg);
                transform: scale(1) rotate(720deg);
            }
        }
        @-webkit-keyframes spinner {
            50% {
                -webkit-transform: rotate(360deg);
                -ms-transform: rotate(360deg);
                transform: rotate(360deg);
            }
            100% {
                -webkit-transform: scale(1) rotate(720deg);
                -ms-transform: scale(1) rotate(720deg);
                transform: scale(1) rotate(720deg);
            }
        }
        @keyframes shadow {
            50% {
                -webkit-transform: scale(0.5);
                -ms-transform: scale(0.5);
                transform: scale(0.5);
                border-color: #e4e4e4;
            }
        }
        @-webkit-keyframes shadow {
            50% {
                -webkit-transform: scale(0.5);
                -ms-transform: scale(0.5);
                transform: scale(0.5);
                border-color: #e4e4e4;
            }
        }
    </style>


